function mascaraDocumento(v){v=v.replace(/\D/g,"");if(v.length<=9){v=v.replace(/(\d{2})(\d)/,"$1.$2");v=v.replace(/(\d{3})(\d)/,"$1.$2");v=v.replace(/(\d{3})(\d{1,2})$/,"$1-$2")}else if(v.length<=11){v=v.replace(/(\d{3})(\d)/,"$1.$2");v=v.replace(/(\d{3})(\d)/,"$1.$2");v=v.replace(/(\d{3})(\d{1,2})$/,"$1-$2")}else{v=v.replace(/^(\d{2})(\d)/,"$1.$2");v=v.replace(/^(\d{2})\.(\d{3})(\d)/,"$1.$2.$3");v=v.replace(/\.(\d{3})(\d)/,".$1/$2");v=v.replace(/(\d{4})(\d)/,"$1-$2")}
return v}
function validarCPF(cpf){if(/^(\d)\1{10}$/.test(cpf))return!1;var soma=0;for(var i=0;i<9;i++){soma+=parseInt(cpf.charAt(i))*(10-i)}
var resto=11-(soma%11);if(resto===10||resto===11)resto=0;if(resto!==parseInt(cpf.charAt(9)))return!1;soma=0;for(var i=0;i<10;i++){soma+=parseInt(cpf.charAt(i))*(11-i)}
resto=11-(soma%11);if(resto===10||resto===11)resto=0;if(resto!==parseInt(cpf.charAt(10)))return!1;return!0}
function validarCNPJ(cnpj){if(/^(\d)\1{13}$/.test(cnpj))return!1;var tamanho=cnpj.length-2;var numeros=cnpj.substring(0,tamanho);var digitos=cnpj.substring(tamanho);var soma=0;var pos=tamanho-7;for(var i=tamanho;i>=1;i--){soma+=numeros.charAt(tamanho-i)*pos--;if(pos<2)pos=9}
var resultado=soma%11<2?0:11-soma%11;if(resultado!==parseInt(digitos.charAt(0)))return!1;tamanho++;numeros=cnpj.substring(0,tamanho);soma=0;pos=tamanho-7;for(var i=tamanho;i>=1;i--){soma+=numeros.charAt(tamanho-i)*pos--;if(pos<2)pos=9}
resultado=soma%11<2?0:11-soma%11;if(resultado!==parseInt(digitos.charAt(1)))return!1;return!0}
function exibirErro(mensagem,input){var msgDiv=document.getElementById("msg");msgDiv.innerHTML=mensagem;msgDiv.style.color="red";input.style.border="solid 1px red"}
function exibirSucesso(input){var msgDiv=document.getElementById("msg");msgDiv.innerHTML="";input.style.border=""}
function validarPreenchimento(input,nome){var regex=/^[A-Za-zÀ-ÖØ-öø-ÿ\s.'-]+$/;if(input.value.trim()===""){exibirErro(nome+" não pode ser vazio!",input);return!1}
exibirSucesso(input);return!0}
function validarDocumento(input){documento=input.value.replace(/\D/g,"");if(input.value.trim()===""){exibirErro("CPF/CNPJ não pode ser vazio!",input);return!1}else if(documento.length<=11){if(!validarCPF(documento)){exibirErro("CPF Inválido!",input);return!1}}else if(documento.length>=14){if(!validarCNPJ(documento)){exibirErro("CNPJ Inválido!",input);return!1}}else{exibirErro("Documento Inválido!",input);return!1}
exibirSucesso(input);return!0}